var bignum=require("bignumber.js"),nextFrame=require("next-frame"),FHEMath=require("../math/FHEMath"),src=require("../src/src"),CRT=require("../math/CRT");exports.EvalAdd=function(r,e){r=FHEMath.vectortoBigNumber(r),e=FHEMath.vectortoBigNumber(e);for(var t=new Array(r.length),u=0;u<r.length;u++){for(var a=new Array(r[0].length),n=0;n<r[0].length;n++){var o=r[u][n].plus(e[u][n]);if(o.gt(src.modulus[u])){for(;o.gt(src.modulus[u]);)o=o.minus(src.modulus[u]);a[n]=o}else a[n]=o}t[u]=a}return FHEMath.vectortoString(t)};